CREATE TABLE "public"."user_chat" ("id" serial NOT NULL, "chat_uuid" uuid NOT NULL, "user_id" integer NOT NULL, "role_id" integer NOT NULL, "textContent_user_query" text NOT NULL, "textContent_assistant_response" text, "textContent_execution_result" text, "textContent_assistant_formatted_response" text, "recordHash" text NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);
